import React from 'react';
import Logincomponent from './Login.css';
const LoginComponent=()=>
{
    var userData=[
    {userName:"Raji",passcode: 123,cart: []},
    {userName:"yuga",passcode: 126,cart: []},
    {userName:"yoga",passcode: 129,cart: []},
    ];
  return<p>welcome to the login page!</p>;

function Login()
{
          var userName=document.getElementById("userName").Value;
             var userPasscode=document.getElementById("passcode").Value;
             var activeUserDetail=userData.filter((data)=>
                {
                return userName==data.userName;
                });
             console.log(activeUserDetail);
             if(activeUserDetail.length>0)
                {
                if(activeUserDetail[0].passcode==userPasscode)
                {
                    localStorage.setItem("userName",activeUserDetail[0].userName);
                }
                else
                {
                   console.log("WRONG PASSCODE") ;
                }
             }
        }
    return(
        <div className="login-container">
            <div className="login-box">
                <h2>Login</h2>
                <LoginComponent/>
                <input type="text"placeholder="userName"/>
                <input type="passcode" placeholder="passcode"/>
                <button onClick ="Login-button">LOGIN </button>
                </div>
                </div>
    );
}
export default Login;
